import React, {useState, useEffect} from "react";
import axios from "axios";
import Level from "../../assets/img/wally1.jpg";
import Modal from "../Modal/Modal";
import "./Levels.css";

function Level1() {
	const [coordsY, setCoordsY] = useState({});
	const [coordsX, setCoordsX] = useState({});
	const [timer, setTimer] = useState(1);
	const [found, setFound] = useState(false);

	useEffect(() => {
		axios.get("/API/level1").then(res => {
			setCoordsY(res.data.coordsY);
			setCoordsX(res.data.coordsX);
		});
	}, []);

	useEffect(() => {
		const time = setTimeout(() => {
			setTimer(timer + 1);
		}, 1000);

		return () => clearTimeout(time);
	}, []);

	// const time = setInterval(() => {
	// 	setTimer(timer + 1);
	// }, 1000);

	console.log(timer);

	const eventHandler = e => {
		if (
			e.nativeEvent.offsetY >= coordsY.min &&
			e.nativeEvent.offsetY <= coordsY.max &&
			e.nativeEvent.offsetX >= coordsX.min &&
			e.nativeEvent.offsetX <= coordsX.max
		) {
			// clearInterval(time);
			console.log("You did ut!");
			setFound(true);
			// clearTimeout(time);
		}
	};

	return (
		<>
			<div className="game">
				<img src={Level} alt="Level 1" onClick={eventHandler} className="level-img" />
			</div>
			{found ? <Modal time={timer} /> : null}
		</>
	);
}

export default Level1;
