import React, {useState, useEffect} from "react";
import axios from "axios";
import Level from "../../assets/img/wally1.jpg";

function Level1() {
	const [coordsY, setCoordsY] = useState([]);
	const [coordsX, setCoordsX] = useState([]);
	const [timer, setTimer] = useState(0);

	useEffect(() => {
		fetch("/API/level1")
			.then(res => res.data)
			.then(data => setCoordsY(data.coordsY));
	}, [coordsY, coordsX]);

	console.log(coordsX.min);

	const eventHandler = e => {
		console.log(coordsX.min);
		console.log(e.nativeEvent.offsetX);
		// console.log("hey");
		e.nativeEvent.offsetY >= coordsY.min &&
		e.nativeEvent.offsetY <= coordsY.max &&
		e.nativeEvent.offsetX >= coordsX.min &&
		e.nativeEvent.offsetX <= coordsX.max
			? console.log("You did ut!")
			: console.log("Oops");
	};

	return (
		<>
			<header></header>
			<div>
				{coordsY.min ? <img src={Level} alt="Level 1" onClick={eventHandler} /> : "Loading"}
			</div>
		</>
	);
}

export default Level1;
